import { Client } from 'gqless';
/**
 * useQuery
 *
 * This hook returns query from gqless.
 *
 * @example
 * import { useQuery } from 'gqless-hook';
 * import { client } from '../graphql'; // generated by @gqless/cli
 *
 * const Component = () => {
 *   const query = useQuery(client);
 *   return (
 *     <div>{query.foo}</div>
 *   );
 * };
 */
export declare const useQuery: <Data extends object>(client: Client<Data>) => Data;
